package com.example.auta.domain.entities;import lombok.*;import org.hibernate.annotations.Cascade;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.util.ArrayList;import java.util.List;import java.util.UUID;import static org.hibernate.annotations.CascadeType.ALL;@Entity@Data@NoArgsConstructor@Builder@AllArgsConstructorpublic class CompanyEntity {    @Id    @GeneratedValue(generator = "uuid")    @GenericGenerator(name = "uuid", strategy = "org.hibernate.id.UUIDGenerator")    private UUID id;    private String name;    private String domain;    private String address;    private String owner;    private byte[] logotype;    @Builder.Default    @OneToMany(cascade = CascadeType.PERSIST, orphanRemoval = true)    @JoinColumn(name="company_id")    private List<BranchEntity> branches = new ArrayList<>();}