package com.example.auta.domain.entities;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.util.HashSet;import java.util.List;import java.util.Set;import java.util.UUID;@Entity@Data@NoArgsConstructor@Builder@AllArgsConstructorpublic class CustomerEntity {    @Id    @GeneratedValue(generator = "uuid")    @GenericGenerator(name = "uuid", strategy = "org.hibernate.id.UUIDGenerator")    private UUID id;    private String forename;    private String surname;    private String email;    private String address;    @Builder.Default    @OneToMany(cascade = CascadeType.PERSIST, orphanRemoval = true)    @JoinColumn(name = "customer_id")    private Set<ReservationEntity> reservations = new HashSet<>();}