package com.example.auta.domain.repositories;import com.example.auta.domain.entities.BranchEntity;import com.example.auta.domain.entities.CustomerEntity;import com.example.auta.domain.entities.EmployeeEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;import java.util.Set;import java.util.UUID;@Repositorypublic interface CustomerRepository extends JpaRepository<CustomerEntity, UUID> {    Optional<CustomerEntity> findCustomerEntityByFornameEqualsAndLastnameEqualsAndEmailEquals(String forname,String lastname,String email);    Set<CustomerEntity> findCustomerEntitiesByReservations_Rent_RentalBranchOrReservations_Retu_ReturnBranch(BranchEntity rentalBranch,                                                                                                             BranchEntity returnBranch);    default Set<CustomerEntity> findCustomerEntitiesByBranch(BranchEntity branch){        return findCustomerEntitiesByReservations_Rent_RentalBranchOrReservations_Retu_ReturnBranch(branch, branch);    }}