package com.example.auta.domain.repositories;import com.example.auta.domain.entities.BranchEntity;import com.example.auta.domain.entities.CompanyEntity;import com.example.auta.domain.entities.CustomerEntity;import com.example.auta.domain.entities.EmployeeEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;import java.util.Set;import java.util.UUID;@Repositorypublic interface CustomerRepository extends JpaRepository<CustomerEntity, UUID> {    Optional<CustomerEntity> findCustomerEntityByForenameEqualsAndSurnameEqualsAndEmailEquals(String forename,                                                                                              String surname,                                                                                              String email);    Set<CustomerEntity> findCustomerEntitiesByReservationsRentalBranchInOrReservationsReturnBranchIn(Set<BranchEntity> rentalBranches,                                                                                                     Set<BranchEntity> returnBranches);    default Set<CustomerEntity> findCustomerEntitiesByCompany(CompanyEntity company){        return findCustomerEntitiesByReservationsRentalBranchInOrReservationsReturnBranchIn(company.getBranches(),                                                                                            company.getBranches());    }}