package com.example.auta.domain.repositories;import com.example.auta.domain.entities.EmployeeEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.stereotype.Repository;import java.util.Optional;import java.util.UUID;@Repositorypublic interface EmployeeRepository extends JpaRepository<EmployeeEntity, UUID> {    Optional<EmployeeEntity> findEmployeeEntityByForenameEqualsAndSurnameEquals(String forename, String surname);}