package com.example.auta.domain.repositories;import com.example.auta.domain.entities.CarEntity;import com.example.auta.domain.entities.CustomerEntity;import com.example.auta.domain.entities.ReservationEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.Optional;import java.util.Set;import java.util.UUID;@Repositorypublic interface ReservationRepository extends JpaRepository<ReservationEntity, UUID> {    Set<ReservationEntity> findAllByCustomer(CustomerEntity customer);    Optional<ReservationEntity> findReservationEntityByCustomerEqualsAndCarEqualsAndRentalStartDateEquals(CustomerEntity customerEntity,                                                                                                          CarEntity car,                                                                                                          LocalDate startDate);}