package com.example.auta.controllers;import com.example.auta.domain.entities.BranchEntity;import com.example.auta.domain.entities.CompanyEntity;import com.example.auta.domain.entities.CustomerEntity;import com.example.auta.domain.entities.ReservationEntity;import com.example.auta.domain.repositories.BranchRepository;import com.example.auta.domain.repositories.CompanyRepository;import com.example.auta.domain.repositories.ReservationRepository;import com.example.auta.models.classes.Branch;import com.example.auta.models.classes.Company;import com.example.auta.models.classes.Customer;import org.junit.After;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.boot.test.web.client.TestRestTemplate;import org.springframework.core.ParameterizedTypeReference;import org.springframework.http.HttpMethod;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.test.context.junit4.SpringRunner;import java.util.*;import static org.junit.Assert.*;@RunWith(SpringRunner.class)@SpringBootTest(webEnvironment = SpringBootTest.WebEnvironment.RANDOM_PORT)public class CompanyControllerTest {    @Autowired    private TestRestTemplate testRestTemplate;    @Autowired    private CompanyRepository companyRepository;    @Autowired    private BranchRepository branchRepository;    @Autowired    private ReservationRepository reservationRepository;    @After    public void tearDown(){        reservationRepository.deleteAll();        branchRepository.deleteAll();        companyRepository.deleteAll();    }    @Test    public void addCompany() {        Company company = Company.builder().name("WiesiekCar").build();        ResponseEntity<String> re = testRestTemplate.postForEntity("/company/add",                                                                   company,                                                                   String.class);        assertEquals(HttpStatus.OK, re.getStatusCode());        String uuid = re.getBody().replace("\"", "");        assertTrue(companyRepository.findById(UUID.fromString(uuid)).isPresent());    }    @Test    public void updateCompany() {        CompanyEntity company = CompanyEntity.builder().name("WiesiekCar").owner("Janusz Wiesław").build();        company = companyRepository.saveAndFlush(company);        String companyUUID = company.getId().toString();        Company updatedCompany = Company.builder().owner("Wiesław Janusz").build();        ResponseEntity<String> re = testRestTemplate                .postForEntity(String.format("/company/%s/update", companyUUID),                               updatedCompany, String.class);        assertEquals(HttpStatus.OK, re.getStatusCode());        company = companyRepository.findById(UUID.fromString(companyUUID)).orElseGet(CompanyEntity::new);        assertEquals(updatedCompany.getOwner(),company.getOwner());    }    @Test    public void listCompanies() {        CompanyEntity company1 = CompanyEntity.builder().name("WiesiekCar").build();        CompanyEntity company2 = CompanyEntity.builder().name("JanuszWóz").build();        companyRepository.saveAll(Arrays.asList(company1,company2));        ResponseEntity<Map<UUID,Company>> re = testRestTemplate                .exchange("/company/list",                          HttpMethod.GET,                          null,                          new ParameterizedTypeReference<Map<UUID, Company>>(){});        Map<UUID,Company> map = re.getBody();        assertEquals(HttpStatus.OK, re.getStatusCode());        assertEquals(2, map.size());    }    @Test    public void deleteBranch() {        CompanyEntity company = CompanyEntity.builder().name("WiesiekCar").build();        BranchEntity branch = BranchEntity.builder().address("ul. Cebulowa 34, 04-123 Warszawa").build();        company.getBranches().add(branch);        company = companyRepository.saveAndFlush(company);        String companyUUID = company.getId().toString();        String branchUUID = company.getBranches().iterator().next().getId().toString();        testRestTemplate.delete(String.format("/company/%s/deletebranch/%s",                                              companyUUID, branchUUID));        assertTrue(!branchRepository.findById(UUID.fromString(branchUUID)).isPresent());    }    @Test    public void addBranch() {        CompanyEntity company = CompanyEntity.builder().name("WiesiekCar").build();        UUID uuid = companyRepository.saveAndFlush(company).getId();        Branch branch = Branch.builder().address("ul. Cebulowa 34, 04-123 Warszawa").build();        ResponseEntity<String> re = testRestTemplate                .postForEntity(String.format("/company/%s/addbranch/",uuid.toString()),                               branch, String.class);        assertEquals(HttpStatus.OK, re.getStatusCode());        String branchUUID = re.getBody().replace("\"", "");        assertTrue(branchRepository.findById(UUID.fromString(branchUUID)).isPresent());    }    @Test    public void deleteCompany() {        CompanyEntity company = CompanyEntity.builder().name("WiesiekCar").build();        UUID uuid = companyRepository.saveAndFlush(company).getId();        testRestTemplate.delete(String.format("/company/%s/delete",uuid.toString()));        assertTrue(!companyRepository.findById(uuid).isPresent());    }    @Test    public void getCustomer() {        CompanyEntity company = CompanyEntity.builder().name("WiesiekCar").build();        BranchEntity branch = BranchEntity.builder().address("ul. Cebulowa 34, 04-123 Warszawa").build();        company.getBranches().add(branch);        company = companyRepository.saveAndFlush(company);        branch = company.getBranches().iterator().next();        CustomerEntity customer1 = CustomerEntity.builder().forname("Adam").build();        CustomerEntity customer2 = CustomerEntity.builder().forname("Kazimierz").build();        ReservationEntity reservation1 = ReservationEntity.builder().customer(customer1).rentalBranch(branch).build();        ReservationEntity reservation2 = ReservationEntity.builder().customer(customer2).returnBranch(branch).build();        Arrays.asList(reservation1, reservation2).forEach(reservationRepository::saveAndFlush);        ResponseEntity<Map<UUID,Customer>> re = testRestTemplate.exchange(                String.format("/company/%s/customerlist",company.getId()),                HttpMethod.GET,                null,                new ParameterizedTypeReference<Map<UUID, Customer>>(){});        Map<UUID,Customer> customers = re.getBody();        assertEquals(HttpStatus.OK, re.getStatusCode());        assertEquals(2,customers.size());    }}